package com.eljumillano.altapromos.ui.home
data class Client( val id: String = "", val name: String = "", val address: String = "", val locality: String = "", val reparto: String = "", val phone: String = "", val email: String = "", val time: String = "", val status: ClientStatus = ClientStatus.NEW, val timestamp: Long = System.currentTimeMillis() )
enum class ClientStatus { NEW, PENDING, COMPLETED }

package com.eljumillano.altapromos.ui.home

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.recyclerview.widget.DiffUtil
import androidx.recyclerview.widget.ListAdapter
import androidx.recyclerview.widget.RecyclerView
import com.eljumillano.altapromos.R
import com.google.android.material.chip.Chip

class ClientsAdapter(private val onClick: (Client) -> Unit) :
    ListAdapter<Client, ClientsAdapter.VH>(Diff()) {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): VH {
        val v = LayoutInflater.from(parent.context).inflate(R.layout.item_client, parent, false)
        return VH(v, onClick)
    }

    override fun onBindViewHolder(holder: VH, position: Int) = holder.bind(getItem(position))

    class VH(itemView: View, private val onClick: (Client) -> Unit) : RecyclerView.ViewHolder(itemView) {
        private val name: TextView = itemView.findViewById(R.id.tvClientName)
        private val address: TextView = itemView.findViewById(R.id.tvClientAddress)
        private val reparto: TextView = itemView.findViewById(R.id.tvReparto)
        private val localidad: TextView = itemView.findViewById(R.id.tvLocalidad)
        private val time: TextView = itemView.findViewById(R.id.tvTime)
        private val chip: Chip = itemView.findViewById(R.id.chipStatus)

        fun bind(c: Client) {
            name.text = c.name
            address.text = c.address
            reparto.text = c.reparto
            localidad.text = c.locality
            time.text = c.time
            when (c.status) {
                ClientStatus.NEW -> {
                    chip.setText(R.string.status_new)
                    chip.setChipBackgroundColorResource(R.color.purple_500)
                }
                ClientStatus.PENDING -> {
                    chip.setText(R.string.status_pending)
                    chip.setChipBackgroundColorResource(R.color.accent_blue)
                }
                ClientStatus.COMPLETED -> {
                    chip.setText(R.string.status_completed)
                    chip.setChipBackgroundColorResource(R.color.teal_700)
                }
            }
            itemView.setOnClickListener { onClick(c) }
        }
    }

    private class Diff : DiffUtil.ItemCallback<Client>() {
        override fun areItemsTheSame(oldItem: Client, newItem: Client) = oldItem.id == newItem.id
        override fun areContentsTheSame(oldItem: Client, newItem: Client) = oldItem == newItem
    }
}

package com.eljumillano.altapromos.ui.notifications
import android.os.Bundle import android.view.LayoutInflater import android.view.View import android.view.ViewGroup import android.widget.ProgressBar import android.widget.TextView import androidx.fragment.app.Fragment import com.eljumillano.altapromos.databinding.FragmentNotificationsBinding
class NotificationsFragment : Fragment() { private var _binding: FragmentNotificationsBinding? = null private val binding get() = _binding!!
override fun onCreateView( inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle? ): View { _binding = FragmentNotificationsBinding.inflate(inflater, container, false) loadMockStats() return binding.root }
private fun loadMockStats() { // Datos de ejemplo - cuando tengas la API reemplazas esto val totalClients = 24 val newClients = 8 val completedClients = 16
val dispenserYes = 15 val dispenserNo = 8 val dispenserTotal = dispenserYes + dispenserNo
val voucherCount = 13 val promo50Count = 11 val promoTotal = voucherCount + promo50Count
// Actualizar contadores principales binding.tvTotalCount.text = totalClients.toString() binding.tvNewCount.text = newClients.toString() binding.tvCompletedCount.text = completedClients.toString()
// Dispenser stats val dispenserYesPercent = (dispenserYes * 100 / dispenserTotal) val dispenserNoPercent = (dispenserNo * 100 / dispenserTotal)
binding.progressDispenserYes.progress = dispenserYesPercent binding.tvDispenserYesCount.text = "$dispenserYes ($dispenserYesPercent%)"
binding.progressDispenserNo.progress = dispenserNoPercent binding.tvDispenserNoCount.text = "$dispenserNo ($dispenserNoPercent%)"
// Promo types stats val voucherPercent = (voucherCount * 100 / promoTotal) val promo50Percent = (promo50Count * 100 / promoTotal)
binding.progressVoucher.progress = voucherPercent binding.tvVoucherCount.text = "$voucherCount ($voucherPercent%)"
binding.progressPromo50.progress = promo50Percent binding.tvPromo50Count.text = "$promo50Count ($promo50Percent%)" }
override fun onDestroyView() { super.onDestroyView() _binding = null } }

